/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `BeforeInsertReview` BEFORE INSERT ON `reviews` FOR EACH ROW BEGIN
    -- Prevent duplicate review from the same user for the same book
    IF EXISTS (
        SELECT 1 FROM reviews 
        WHERE book_id = NEW.book_id AND user_id = NEW.user_id
    ) THEN
        SIGNAL SQLSTATE '45000'
        SET MESSAGE_TEXT = 'User has already reviewed this book';
    END IF;

    -- Set default rating if NULL
    IF NEW.rating IS NULL THEN
        SET NEW.rating = 3;
    END IF;
END */;;
