CREATE DEFINER=`root`@`localhost` PROCEDURE `GetAuthorReviewCounts`()
BEGIN
    DECLARE done INT DEFAULT 0;
    DECLARE author_id_var INT;
    DECLARE author_name_var VARCHAR(255);
    DECLARE review_count_var INT;

    DECLARE cur CURSOR FOR
    SELECT a.author_id, a.author_name, COUNT(r.review_id) AS review_count
    FROM authors a
    JOIN book_authorships ba ON a.author_id = ba.author_id
    JOIN books b ON ba.book_id = b.book_id
    LEFT JOIN reviews r ON b.book_id = r.book_id
    GROUP BY a.author_id;

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;

    CREATE TEMPORARY TABLE IF NOT EXISTS AuthorReviewCounts (
        author_id INT,
        author_name VARCHAR(255),
        review_count INT
    );

    OPEN cur;

    read_loop: LOOP
        FETCH cur INTO author_id_var, author_name_var, review_count_var;
        IF done THEN
            LEAVE read_loop;
        END IF;

        INSERT INTO AuthorReviewCounts (author_id, author_name, review_count)
        VALUES (author_id_var, author_name_var, review_count_var);
    END LOOP;

    CLOSE cur;

    SELECT * FROM AuthorReviewCounts;

END ;;
