CREATE DEFINER=`root`@`localhost` PROCEDURE `GetRecommendedBooks`(IN user_id INT, OUT recommended_books TEXT)
BEGIN
    DECLARE top_genre INT;
    DECLARE book_count INT;

    -- Initialize output
    SET recommended_books = '';

    -- Get the user's top genre
    SET top_genre = (SELECT GetUserTopGenre(user_id));

    -- If user has no top genre, return a message
    IF top_genre IS NULL OR top_genre = 0 THEN
        SET recommended_books = 'No recommendations available. Try reviewing some books first!';
    ELSE
        -- Ensure the temp table does not exist before creating
        DROP TEMPORARY TABLE IF EXISTS Temp_RecommendedBooks;
        CREATE TEMPORARY TABLE Temp_RecommendedBooks (book_title VARCHAR(255));

        -- Call another procedure to populate the table
        CALL GetBooksByGenre(top_genre, 5);

        -- Count how many books were retrieved
        SELECT COUNT(*) INTO book_count FROM Temp_RecommendedBooks;

        -- If no books found, return a message
        IF book_count = 0 THEN
            SET recommended_books = 'No books found for this genre.';
        ELSE
            -- Use GROUP_CONCAT to efficiently concatenate book titles
            SELECT GROUP_CONCAT(book_title SEPARATOR ', ') INTO recommended_books FROM Temp_RecommendedBooks;
        END IF;

        -- Cleanup
        DROP TEMPORARY TABLE IF EXISTS Temp_RecommendedBooks;
    END IF;
END ;;
