CREATE DEFINER=`root`@`localhost` PROCEDURE `GetTopRatedBooks`(IN minRating DECIMAL(3,2))
BEGIN
    SELECT 
        b.book_id, 
        b.book_title, 
        ROUND(AVG(r.rating), 2) AS avg_rating
    FROM books b
    JOIN reviews r ON b.book_id = r.book_id
    GROUP BY b.book_id
    HAVING avg_rating >= minRating
    ORDER BY avg_rating DESC;
